import { useEffect, useState } from "react";

function Hello() {
  useEffect(() => {
    console.log("i'm here");
  }, []);
  return <h1>Hello</h1>;
}
function App() {
  const [showing, setShowing] = useState(false);
  const onClick = () => setShowing((prev) => !prev);
  return (
    <div className="App">
      {showing ? <Hello /> : null}
      <button onClick={onClick}> {showing ? "hide" : "show"} </button>
    </div>
  );
}

export default App;
